mapper.select:
select  from drug_admin ad,drug_company co where ad.com_id=co.com_id and ad.isva='1'

	and ad.admin_name like CONCAT(CONCAT('%',#{admin.adminName,jdbcType=VARCHAR},'%'))


	and co.com_id = #{admin.comId,jdbcType=VARCHAR}

order by (ad.sort+0) desc 
limit #{pageUtil.startRows,jdbcType=INTEGER},#{pageUtil.rows,jdbcType=INTEGER}

select count(*) from drug_admin ad,drug_company co where ad.com_id=co.com_id and ad.isva='1'

	and ad.admin_name like CONCAT(CONCAT('%',#{adminName,jdbcType=VARCHAR},'%'))


	and co.com_id = #{comId,jdbcType=VARCHAR}
