 	limit #{pageUtil.startRows,jdbcType=INTEGER},#{pageUtil.rows,jdbcType=INTEGER}

select
from drug_audit where qc_fk_id = #{comId,jdbcType=VARCHAR} and aud_fk_id = #{audFkId,jdbcType=VARCHAR}
and aud_state in ('10110','10111','10112','10113')
and aud_state in ('10010','10011','10012','10013')
order by (sort+0) desc limit #{pageUtil.startRows,jdbcType=INTEGER},#{pageUtil.rows,jdbcType=INTEGER}

