mapper.select:
 select 
 
 from drug_drug dd,drug_drug_type dt
 where dd.dt_id = dt.dt_id and dd.isva = '1'
	
		and dd.drug_name like CONCAT(CONCAT('%',#{record.drugName,jdbcType=VARCHAR},'%'))
	
	
		and dt.dt_id = #{record.dtId,jdbcType=VARCHAR}
	
	
		and dd.drug_price > #{record.priceMin,jdbcType=DECIMAL}
	
	
		and dd.drug_price < #{record.priceMax,jdbcType=DECIMAL}
	
	order by (dd.sort+0) desc 
	limit #{pageUtil.startRows,jdbcType=INTEGER},#{pageUtil.rows,jdbcType=INTEGER}

 select 
 count(*) 
 from drug_drug dd,drug_drug_type dt
 where dd.dt_id = dt.dt_id and dd.isva = '1'
	
		and dd.drug_name like CONCAT(CONCAT('%',#{drugName,jdbcType=VARCHAR},'%'))
	
	
		and dt.dt_id = #{dtId,jdbcType=VARCHAR}
	
	
		and dd.drug_price > #{priceMin,jdbcType=DECIMAL}
	
	
		and dd.drug_price < #{priceMax,jdbcType=DECIMAL}

 select 
 
 from drug_drug dd,drug_drug_type dt,drug_drug_inv di,drug_company dc
 where dd.dt_id = dt.dt_id and di.drug_id=dd.drug_id and dc.com_id=di.com_id and dd.isva = '1'
	
		and dd.drug_name like CONCAT(CONCAT('%',#{record.drugName,jdbcType=VARCHAR},'%'))
	
	
		and dt.dt_id = #{record.dtId,jdbcType=VARCHAR}
	
	
		and dd.drug_price > #{record.priceMin,jdbcType=DECIMAL}
	
	
		and dc.com_id = #{record.comId,jdbcType=VARCHAR}
	
	
		and dd.drug_price < #{record.priceMax,jdbcType=DECIMAL}
	
	order by (dd.sort+0) desc 
	limit #{pageUtil.startRows,jdbcType=INTEGER},#{pageUtil.rows,jdbcType=INTEGER}

 select 
 
 from drug_drug dd,drug_audit da,drug_drug_type dt
 where dd.drug_id = da.aud_fk_id and dd.dt_id = dt.dt_id and dd.isva = '1' and da.isva = '1'
	
		and dd.drug_name like CONCAT(CONCAT('%',#{record.drugName,jdbcType=VARCHAR},'%'))
	
	
		and dd.drug_price > #{record.priceMin,jdbcType=DECIMAL}
	
	
		and dd.drug_price < #{record.priceMax,jdbcType=DECIMAL}
	
	order by (da.sort+0) desc 
	limit #{pageUtil.startRows,jdbcType=INTEGER},#{pageUtil.rows,jdbcType=INTEGER}

select count(dd.drug_id) from drug_drug dd,drug_audit da,drug_drug_type dt where dd.drug_id = da.aud_fk_id and dd.dt_id = dt.dt_id and dd.isva = '1' and da.isva = '1'
and dd.drug_name like CONCAT(CONCAT('%',#{drugName,jd4 bcType=VARCHAR},'%'))
and dd.drug_price > #{priceMin,jdbcType=DECIMAL}
and dd.drug_price < #{priceMax,jdbcType=DECIMAL}

 select 
 
 from drug_drug dd,drug_audit da,drug_drug_type dt 
 where dd.drug_id = da.aud_fk_id and dd.dt_id = dt.dt_id and da.aud_state = '10011' and dd.isva = '1' and da.isva = '1'
	
		and dd.drug_name like CONCAT(CONCAT('%',#{record.drugName,jdbcType=VARCHAR},'%'))
	
	
		and dt.dt_id = #{record.dtId,jdbcType=VARCHAR}
	
	
		and dd.drug_price > #{record.priceMin,jdbcType=DECIMAL}
	
	
		and dd.drug_price < #{record.priceMax,jdbcType=DECIMAL}
	
