      )
    values (#{drugId,jdbcType=VARCHAR}, #{dtId,jdbcType=VARCHAR}, 
      #{drugName,jdbcType=VARCHAR}, #{drugUnit,jdbcType=VARCHAR}, #{drugProp,jdbcType=VARCHAR}, 
