mapper.select:
	select * from drug_module where isva ='是'
	  
	   and mode_id=#{module.modeId}
	  
	  
     and dru_mode_id = #{module.druModeId}
    
    
     and mode_url = #{module.modeUrl}
    
    
     and mode_name like CONCAT(CONCAT('%',#{module.modeName},'%'))
    
    
     and mode_code = #{module.modeCode}
    
    
     and mode_explain = #{module.modeExplain}
    
    order by (sort+0) desc 
 	  limit #{page.startRows},#{page.rows}

	select count(*) from drug_module where isva ='是'
	
	   and mode_id=#{modeId}
	  
	  
     and dru_mode_id = #{druModeId}
    
    
     and mode_url = #{modeUrl}
    
    
     and mode_name like CONCAT(CONCAT('%',#{modeName},'%'))
    
    
     and mode_code = #{modeCode}
    
    
     and mode_explain = #{modeExplain}
