mapper.insert:
insert into drug_sale (sale_id, drug_id, sale_time, 
  sale_amount, sale_money, isva, 
  optime, oper, sort
  )
values (#{saleId,jdbcType=VARCHAR}, #{drugId,jdbcType=VARCHAR}, #{saleTime,jdbcType=TIMESTAMP}, 
  #{saleAmount,jdbcType=INTEGER}, #{saleMoney,jdbcType=DECIMAL}, #{isva,jdbcType=VARCHAR}, 
  #{optime,jdbcType=TIMESTAMP}, #{oper,jdbcType=VARCHAR}, #{sort,jdbcType=VARCHAR}
  )

mapper.select:
	SELECT sa.*,dr.drug_name FROM drug_sale sa,drug_drug dr WHERE sa.drug_id=dr.drug_id AND sa.isva = '1'
	  
     and dr.drug_Name = #{sale.drug_Name,jdbcType=VARCHAR}
    
    
     and sa.sale_time = #{sale.saleTime,jdbcType=TIMESTAMP}
    
    
     and sa.sale_amount = #{sale.saleAmount,jdbcType=VARCHAR}
    
    
     and sa.sale_money > #{sale.priceMin,jdbcType=DECIMAL}
    
     
     and sa.sale_money < #{sale.priceMax,jdbcType=DECIMAL}
    
	 order by (sa.sort+0) desc
	 limit #{util.startRows},#{util.rows}
